<?phpinclude 'common/useful_stuff.php';if (!db_connect())	die();		if ($_uid == 0){	echo"<p>&nbsp;</p>	<div class='badge'>	<p>&nbsp;</p><p>Welcome to Old Skater Club </p><p> <a href='register.php'>Sign Up Here</a></p>			</div>			<p>&nbsp;</p>";		} $user_id =  "$_uid";$photo_who = getvar("photo_who");$photo_where = getvar("photo_where");$photo_when = getvar("photo_when");$description = getvar("description");$photo=($_FILES['uploadfile']['name']); //This is the directory where images will be saved //$target = $target . basename( $_FILES['uploadfile']['name']); // This is the temporary file created by PHP$uploadedfile = $_FILES['uploadfile']['tmp_name'];//check file type if($_FILES['uploadfile']['type'] =='image/gif' || 	$_FILES['uploadfile']['type'] =='image/png' || 			$_FILES['uploadfile']['type'] =='image/pjpeg' ||				$_FILES['uploadfile']['type'] =='image/tiff')				{	die('<p class="error" style="font-family:sans-serif;"><span style="color:#ff1900; font-family:sans-serif;"><b>Wrong file type.</b> </span>Please Use jpeg or jpg.<br />	If you are not automatically redirected <a href="javascript:history.go(-1)">Click here</a><br />You will be redirected back.	<meta http-equiv="refresh" content="4;URL=javascript:history.go(-1)"></p>');}// Create an Image from it so we can do the resize$src = imagecreatefromjpeg($uploadedfile);// Capture the original size of the uploaded imagelist($width,$height)=getimagesize($uploadedfile);// For our purposes, I have resized the image to be// 600 pixels wide, and maintain the original aspect// ratio. This prevents the image from being "stretched"// or "squashed". If you prefer some max width other than// 600, simply change the $newwidth variable$newwidth=300;$newheight=($height/$width)*275;$tmp=imagecreatetruecolor($newwidth,$newheight);// this line actually does the image resizing, copying from the original// image into the $tmp imageimagecopyresampled($tmp,$src,0,0,0,0,$newwidth,$newheight,$width,$height);// now write the resized image to disk. I have assumed that you want the// resized, uploaded image file to reside in the ./images subdirectory.$filename = "images/". $_FILES['uploadfile']['name'];imagejpeg($tmp,$filename,100);imagedestroy($src);imagedestroy($tmp); // NOTE: PHP will clean up the temp file it created when the request// has completed.// Connects to your Database // mysql_connect("localhost", "root", "root") or die(mysql_error()) ; // mysql_select_db("dev") or die(mysql_error()) ; //Writes the information to the database mysql_query("INSERT INTO images (user_id, name, photo_who, photo_where, photo_when, description) 	VALUES ('{$user_id}', '{$photo}' , '{$photo_who}' , '{$photo_where}' , '{$photo_when}' , '{$description}'		)") ; 		HEADER("LOCATION:myprofile.php?user_id=$_uid");			die(); ?>